--  DDL for View CPTYEPEVIEW
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "RKNET"."CPTYEPEVIEW" ("PNLSETNAME", "MTMDATE", "COUNTERPARTY", "PD", "LGD", "EPE") AS 
  SELECT PNLSetName ,
          MTMDate ,
          ShortName Counterparty  ,
          (CASE 
                WHEN PD IS NOT NULL THEN PD
          ELSE 100
             END) PD  ,
          (CASE 
                WHEN LGD_CPTY IS NOT NULL THEN LGD_CPTY
                WHEN LGD_ASSET IS NOT NULL THEN LGD_ASSET
          ELSE 100
             END) LGD  ,
          EPE 
     FROM ( SELECT EPE_CPTY.PNLSetName ,
                   EPE_CPTY.MTMDate ,
                   Cpty.ShortName ,
                   ( SELECT MAX(ProbabilityDefault) 
                     FROM Rating 
                            JOIN CptyRating 
                             ON Rating.Id = CptyRating.RatingId_LongTerm
                            AND Rating.RatingAgencyId = CptyRating.RatingAgencyId
                            AND CptyRating.CptyId = Cpty.Id
                            JOIN RatingAgency 
                             ON Rating.RatingAgencyId = RatingAgency.Id
                            AND RatingAgency.InternalRatingAgency = 1
                      WHERE ( Rating.Term = 'L'
                              OR Rating.Term = 'A' ) ) PD  ,
                   ( SELECT MAX(LGD) 
                     FROM LossGivenDefault 
                      WHERE Approach = 'F'
                              AND ( ( CptyRefMode IN ( 'E','H' )

                              AND CptyId = Cpty.Id )
                              OR ( CptyRefMode = 'H'
                              AND EXISTS ( SELECT 1 
                                           FROM CptyLink 
                                            WHERE CptyId_Parent = LossGivenDefault.CptyId
                                                    AND CptyId_Child = Cpty.Id ) ) ) ) LGD_CPTY  ,
                   ( SELECT MAX(LGD) 
                     FROM LossGivenDefault 
                      WHERE Approach = 'F'
                              AND AssetClassId = Cpty.AssetClassId_Fundation
                              AND Seniority = 'N' ) LGD_ASSET  ,
                   EPE_CPTY.EPE 
            FROM Cpty 
                   JOIN EPE_CPTY 
                    ON Cpty.ShortName = EPE_CPTY.Counterparty ) T;
