--------------------------------------------------------

  CREATE OR REPLACE TRIGGER "RKNET"."TDQC_SEQUENCE_TRG" BEFORE INSERT ON TDQC
FOR EACH ROW
DECLARE 
v_newVal NUMBER(12) := 0;
v_incval NUMBER(12) := 0;
BEGIN
  IF INSERTING AND :new.SEQUENCE IS NULL THEN
    SELECT  TDQC_SEQUENCE_SEQ.NEXTVAL INTO v_newVal FROM DUAL;
    -- If this is the first time this table have been inserted into (sequence == 1)
    IF v_newVal = 1 THEN 
      --get the max indentity value from the table
      SELECT NVL(max(SEQUENCE),0) INTO v_newVal FROM TDQC;
      v_newVal := v_newVal + 1;
      --set the sequence to that value
      LOOP
           EXIT WHEN v_incval>=v_newVal;
           SELECT TDQC_SEQUENCE_SEQ.nextval INTO v_incval FROM dual;
      END LOOP;
    END IF;
    -- save this to emulate @@identity
   utils.identity := v_newVal; 
   -- assign the value from the sequence to emulate the identity column
   :new.SEQUENCE := v_newVal;
  END IF;
END;
/
ALTER TRIGGER "RKNET"."TDQC_SEQUENCE_TRG" ENABLE;
