  CREATE OR REPLACE FUNCTION "RKNET"."STRTOBIN" 
(
  v_str IN VARCHAR2
)
RETURN RAW
AUTHID DEFINER
AS
   v_bin RAW(12);

BEGIN
   v_bin := CASE 
                 WHEN SUBSTR(v_str, 1, 2) = '0x' THEN HEXTORAW(SUBSTR(v_str, 2))
   ELSE HEXTORAW(v_str)
      END ;
   RETURN v_bin;
END;

